INSERT INTO `news` (`id`, `title`, `unsigned_title`, `image`, `description`, `content`, `created_at`, `updated_at`) VALUES
(5, 'Giới thiệu', 'gioi-thieu', 'T9eC4-XH01.jpg', 'March Fashion là thương hiệu được xây dựng, phát triển và hoạt động trong lĩnh vực thời trang từ năm 2010, với những trang phục, phụ kiện thời trang...', '<p style=\"text-align: justify;\">March Fashion là thương hiệu được xây dựng, phát triển và hoạt động trong lĩnh vực thời trang từ năm 2010, với những trang phục, phụ kiện thời trang cho giới trẻ với phong cách trẻ trung, hiện đại. March Fashion tự hào là một trong những thương hiệu thời trang đầu tiên đã thường xuyên mang xu hướng thời trang hiện đại về cho giới trẻ tại thị trường Hà Nội và sau này là thị trường toàn quốc.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Với mong muốn có thể phục vụ nhu cầu thời trang trong nước, cung cấp nhiều sản phẩm cho người tiêu dùng với chất lượng tốt và giá cả phải chăng. March Fashion luôn xây dựng một hình ảnh tốt nhất đem đến khách hàng, đi kèm đó là chất lượng ở sản phẩm luôn được kiểm tra kỹ lưỡng và đạt độ hoàn thiện cao trước khi xuất ra thị trường. Chính vì vậy, March Fashion rất tự hào là thương hiệu được rất nhiều khách hàng tin tưởng, thường xuyên sử dụng các sản phẩm của March Fashion. Chúng tôi thường xuyên nhận được những phản hồi tích cực của khách hàng. Chúng tôi luôn coi đó là động lực để ngày một hoàn thiện và cố gắng để phục vụ được nhiều khách hàng hơn, đưa ra thị trường được nhiều sản phẩm chất lượng hơn, giá thành cạnh tranh hơn và luôn luôn là một trong những thương hiệu số 1 về thời trang cho giới trẻ.</p>\r\n\r\n<p style=\"text-align: justify;\">Các sản phẩm được thiết kế và sản xuất của chúng tôi luôn là những sản phẩm nổi bật, thiết kế bởi những designer hàng đầu của March Fashion, sử dụng chất lượng cao cấp nhất. Bên cạnh đó, các dòng sản phẩm đều là Made in Vietnam nhưng giá cả phải chăng hơn rất nhiều các thương hiệu khác, được rất nhiều khách hàng yêu thích.</p>\r\n\r\n<p style=\"text-align: justify;\">Chúng tôi đã, đang, và sẽ nỗ lực hết mình để mang tới những sản phẩm thời trang chất lượng đến với khách hàng, để March Fashion luôn xứng đàng là một trong những thương hiệu thời trang hàng đầu cho giới trẻ</p>\r\n\r\n<p>&nbsp;</p>', '2018-04-16 13:43:53', '2018-06-16 19:02:35'),
(6, 'Tuyển dụng', 'tuyen-dung', '5lzsx-TuyenDung01.jpg', 'Tuyển dụng March Fashion', '<p style=\"text-align:center\"><a href=\"http://localhost:8888/marchfashion/public/uploads/news/5lzsx-TuyenDung01.jpg\"><img alt=\"\" src=\"http://localhost:8888/marchfashion/public/uploads/news/5lzsx-TuyenDung01.jpg\" style=\"height:147px; width:270px\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện nay, March Fashion đang cần tuyển dụng các vị trí:</p>\r\n\r\n<p>- Trưởng nhóm kinh doanh</p>\r\n\r\n<p>- Thiết kế thời trang</p>\r\n\r\n<p>- Tư vấn bán hàng</p>\r\n\r\n<p>Hồ sơ ứng tuyển vui lòng nộp tại văn phòng số 103 Kim Mã, Cầu Giấy.</p>\r\n\r\n<p>Hoặc gửi email về địa chỉ: tuyendung.marchfashion@gmail.com.</p>\r\n\r\n<p>Tham khảo thêm tại page: https://www.facebook.com/tuyendung.marchfashion.</p>\r\n\r\n<p><em>(Các thông tin tuyển dụng được chúng tôi cập nhật tại đây, mời các bạn ghé qua website thường xuyên để nắm bắt các vị trí tuyển dụng của March Fashion.)</em></p>', '2018-04-16 13:44:11', '2018-06-16 19:29:41'),
(8, 'Các cửa hàng', 'cac-cua-hang', '', 'Các cửa hàng tại March Fashion...', '<p>Hiện nay, March Fashion có 3 cửa hàng đều đặt tại Hà Nội với không gian rộng rãi và có chỗ để xe riêng cho khách hàng:</p>\r\n\r\n<p>- Số 103 Kim Mã, Cầu Giấy. SĐT: 04 6294 41 41</p>\r\n\r\n<p>- Số 96 Bà Triệu, Hoàn Kiếm. SĐT: 04 6294 51 51</p>\r\n\r\n<p>- Số 171 Nguyễn Trãi, Thanh Xuân. SĐT: 04 6294 61 61</p>', '2018-06-16 10:57:19', '2018-06-16 19:25:38'),
(9, 'Liên hệ', 'lien-he', '', 'Liên hệ với chúng tôi', '<p>Để giải đáp thắc mắc, phản hồi, cùng những góp ý với March Fashion, quý khách hàng vui lòng gọi đến số điện thoại: 04 6294 41 41 hoặc 04 1003 96 96.</p>\r\n\r\n<p>Hoặc gửi email về địa chỉ: contact.marchfashion@gmail.com.</p>\r\n\r\n<p>Hoặc inbox cho chúng tôi tại trang: www.facebook.com/marchfashion.</p>\r\n\r\n<p>Chúng tôi xin chân thành cảm ơn!</p>', '2018-06-16 10:57:38', '2018-06-16 19:38:29'),
(10, 'Hè về, học cách mặc váy hoa đẹp như mỹ nhân Việt', 'he-ve-hoc-cach-mac-vay-hoa-dep-nhu-my-nhan-viet', 'A7VSe-he-ve-hoc-cac-mac-nhu-my-nhan-viet-edit.jpg', 'Trước thềm mùa hè, dàn sao Việt đồng loạt lăng xê những mẫu váy hoa nữ tính, trẻ trung và thoải mái trong các bức ảnh street style...', '<p>Trước thềm mùa hè, dàn sao Việt đồng loạt lăng xê những mẫu váy hoa nữ tính, trẻ trung và thoải mái trong các bức ảnh street style.</p>\r\n\r\n<div class=\"the-article-body cms-body\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 18px; vertical-align: baseline; background: transparent; text-size-adjust: 100%; float: left; width: 660px; position: relative; font-family: &quot;Noto Serif&quot;, serif; color: rgb(34, 34, 34); line-height: 30px; -webkit-font-smoothing: antialiased;\">\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); float:none; font-family:sans-serif; font-size:14px; line-height:20px; margin:0px 0px 14px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"He ve, hoc cach mac vay hoa dep nhu my nhan Viet hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_03/VH.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:990px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><a class=\"btnSlideshow\" href=\"https://news.zing.vn/he-ve-hoc-cach-mac-vay-hoa-dep-nhu-my-nhan-viet-post831326.html#slideshow\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; vertical-align: baseline; background: rgba(0, 0, 0, 0.3); text-decoration-line: none; color: rgb(85, 85, 85); position: absolute; top: 0px; right: 0px; width: 30px; height: 30px; overflow: hidden; display: block;\">Phóng to</a></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Mùa hè là khoảng thời gian lên ngôi của những mẫu váy hoa nhẹ nhàng. Các nhà mốt thế giới như Miu Miu, Balenciga hay Chloé đều lăng xê thiết kế với chất liệu vải lụa, xuyên thấu cùng màu sắc nổi bật như vàng, đỏ, xanh...</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"He ve, hoc cach mac vay hoa dep nhu my nhan Viet hinh anh 2\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_03/VH2.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:990px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Trang phục họa tiết hoa là item cần có trong tủ quần áo ngày hè của phái nữ.&nbsp;Tuy nhiên, xu hướng này đều thay đổi theo từng năm. Vào mùa hè 2018, thiết kế vẫn mang phom dáng suông thoải mái nhưng được chiết eo nhẹ, nhằm tạo điểm nhấn thon gọn cho vóc dáng người mặc. Thanh Hằng yêu thích việc diện trang phục hoa trong mùa hè. Cô kết hợp cùng phụ kiện túi xách hay mắt kính sành điệu.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"He ve, hoc cach mac vay hoa dep nhu my nhan Viet hinh anh 3\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_03/VH31.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:990px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Xu hướng váy hoa năm 2018 trẻ trung hơn với sự hòa trộn cùng nhiều gam màu nổi bật như vàng, hồng hay xanh pastel. Ngoài ra, kiểu trang phục tay phồng cũng mang lại vẻ cổ điển cho các cô gái khi diện thiết kế như Tăng Thanh Hà.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"He ve, hoc cach mac vay hoa dep nhu my nhan Viet hinh anh 4\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_03/VH3.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:822px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:659.889px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Mùa hè năm nay, những mẫu trang phục hoa còn được thiết kế mang phom dáng của chiếc váy quấn (wrap dress) với phần vạt đắp chéo cùng dây đai buộc lệch. Một chiếc nón cói sẽ giúp các cô gái tăng thêm vẻ nữ tính, trẻ trung.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"He ve, hoc cach mac vay hoa dep nhu my nhan Viet hinh anh 5\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_03/VH4.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:660px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Kiểu váy quấn họa tiết hoa phù hợp với nhiều vóc dáng khác nhau. Không chỉ che lấp khuyết điểm vòng 1 khiêm tốn, thiết kế còn tạo hiệu ứng giúp thắt eo trông thon gọn hơn. Ngoài ra, phần vai và ống tay phồng tăng thêm vẻ cổ điển, nữ tính.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"He ve, hoc cach mac vay hoa dep nhu my nhan Viet hinh anh 6\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_03/VH51.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:824px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:659.689px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Những mẫu váy hai dây kết hợp cùng áo thun đồng điệu màu sắc được xem như xu hướng thời trang trong mùa hè năm nay. Mũ cói và túi tole là phụ kiện không thể thiếu khi diện cùng váy hoa phom dáng suông thoải mái.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"He ve, hoc cach mac vay hoa dep nhu my nhan Viet hinh anh 7\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_03/VH5.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:825px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Với những mẫu váy hở ngực, phái nữ nên tinh tế kết hợp cùng áo thun vải xuyên thấu bên trong.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"He ve, hoc cach mac vay hoa dep nhu my nhan Viet hinh anh 8\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_03/VH6.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:660px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Năm nay, kích cỡ những bông hoa không bó buộc vào chi tiết hoa nhí, mà còn xuất hiện nhiều mẫu hoa bản trung với cách phối màu nền nã. Sự đồng điệu màu sắc giữa phụ kiện và họa tiết là gợi ý thú vị.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>', '2018-06-16 10:59:49', '2018-06-16 11:50:06'),
(11, 'Tín đồ Việt lăng xê xu hướng đồ tắm nào trong mùa hè?', 'tin-do-viet-lang-xe-xu-huong-do-tam-nao-trong-mua-he-', 'gQaBK-B23.jpg', 'Monokini họa tiết, bikini màu sắc đều là những mẫu đồ tắm sành điệu được Khánh Linh, Salim... lựa chọn trong mùa hè nắng nóng oi bức...', '<p>Monokini họa tiết, bikini màu sắc đều là những mẫu đồ tắm sành điệu được Khánh Linh, Salim... lựa chọn trong mùa hè nắng nóng oi bức.</p>\r\n\r\n<div class=\"the-article-body cms-body\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 18px; vertical-align: baseline; background: transparent; text-size-adjust: 100%; float: left; width: 660px; position: relative; font-family: &quot;Noto Serif&quot;, serif; color: rgb(34, 34, 34); line-height: 30px; -webkit-font-smoothing: antialiased;\">\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); float:none; font-family:sans-serif; font-size:14px; line-height:20px; margin:0px 0px 14px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B1.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:825px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\"><strong>Monokini họa tiết:&nbsp;</strong>Mùa hè năm nay, những mẫu đồ tắm họa tiết chấm bi, kẻ sọc, hoa lá nhiệt đới là xu hướng được nhiều người ưa chuộng. Hot girl Salim lăng xê monokini với tông màu tương phản nổi bật.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 2\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B3.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:880px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" title=\"Tín đồ Việt lăng xê xu hướng đồ tắm nào trong mùa hè? hình ảnh 2\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Những mẫu monokini với đường kẻ sọc trắng, giúp thân hình phái nữ trở nên thon gọn hơn. Ngoài ra, đồ tắm họa tiết còn mang đến vẻ trẻ trung của người mặc thông qua gam màu xanh nước biển, vàng hay hồng pastel.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 3\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B10.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:825px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" title=\"Tín đồ Việt lăng xê xu hướng đồ tắm nào trong mùa hè? hình ảnh 3\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Đối với monokini hoa chất liệu vải xuyên thấu, người mặc phải tinh tế khi lựa chọn nội y đi kèm bên trong.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 4\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B9.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:825px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Ngoài&nbsp;việc tạo nên hiệu ứng thị giác bắt mắt, đồ tắm họa tiết còn giúp các cô gái dễ dàng che lấp những khuyết điểm như vòng 1 khiêm tốn, thắt eo không thon gọn...</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 5\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B2.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:825px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" title=\"Tín đồ Việt lăng xê xu hướng đồ tắm nào trong mùa hè? hình ảnh 5\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\"><strong>Monokini đơn sắc:&nbsp;</strong>Không có dấu hiệu lỗi thời, những mẫu đồ tắm đơn sắc vẫn được lòng dàn tín đồ thời trang Việt. Họ biến tấu trở nên mới lại bằng những chi tiết đục lỗ sành điệu.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 6\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B4.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:825px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" title=\"Tín đồ Việt lăng xê xu hướng đồ tắm nào trong mùa hè? hình ảnh 6\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Những cô gái sở hữu vóc dáng mũm mĩm không phù hợp với đồ tắm đơn sắc. Thiết kế sẽ khiến phái nữ dễ dàng lộ rõ nhược điểm vòng eo kém thon gọn.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 7\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B31.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:660px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Để tránh sự nhàm chán với monokini đơn sắc, tín đồ thời trang vẫn có thể diện những mẫu monokini đan dây quyến rũ như Nga Wendy.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 8\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B5.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:825px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\"><strong>Bikini màu sắc:&nbsp;</strong>Đồ tắm hai mảnh màu sắc đang là xu hướng nổi bật trong mùa hè năm nay. Các cô gái sở hữu vòng 1 quá khổ tránh diện thiết kế cúp ngực lấp lánh. Điều này sẽ khiến hình ảnh của bản thân trông kém tinh tế.&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Tin do Viet lang xe xu huong do tam nao trong mua he? hinh anh 9\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/rohunwa/2018_04_02/B12.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:657px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:659.749px\" title=\"Tín đồ Việt lăng xê xu hướng đồ tắm nào trong mùa hè? hình ảnh 9\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Các tín đồ vẫn có thể lựa chọn phụ kiện độc đáo để kết hợp cùng những mẫu bikini màu sắc. Kính gọng tròn, khăn turban họa tiết cũng là xu hướng thời trang trong năm 2018.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>', '2018-06-16 11:02:51', '2018-06-16 11:50:28');
INSERT INTO `news` (`id`, `title`, `unsigned_title`, `image`, `description`, `content`, `created_at`, `updated_at`) VALUES
(12, 'Cập nhật ngay xu hướng sọc ca rô vào tủ quần áo', 'cap-nhat-ngay-xu-huong-soc-ca-ro-vao-tu-quan-ao', 'Rxms9-b24edit.jpg', 'Các tín đồ thời trang một lần nữa được chiêm ngưỡng những hoạ tiết ca rô mới nhất từ sàn diễn của các thương hiệu thời trang đình đám trong mùa xuân hè...', '<p>Các tín đồ thời trang một lần nữa được chiêm ngưỡng những hoạ tiết ca rô mới nhất từ sàn diễn của các thương hiệu thời trang đình đám trong mùa xuân hè.</p>\r\n\r\n<div class=\"the-article-body cms-body\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 18px; vertical-align: baseline; background: transparent; text-size-adjust: 100%; float: left; width: 660px; position: relative; font-family: &quot;Noto Serif&quot;, serif; color: rgb(34, 34, 34); line-height: 30px; -webkit-font-smoothing: antialiased;\">\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); float:none; font-family:sans-serif; font-size:14px; line-height:20px; margin:0px 0px 14px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/5cc05eac4c136b4416e6faa2b53f7b54.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:988px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:659.446px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Sọc ca rô không chỉ thuộc về Burberry. Rõ ràng khi nhắc tới ca rô, giới mộ điệu lại nghĩ ngay tới thương hiệu đình đám Burberry. Nhưng các nhà thiết kế khác cũng trình làng một thế giới đầy sắc màu biến hoá của hoạ tiết này trong mùa xuân hè 2018. &nbsp;Tuy nhiên, năm nay xu hướng sọc ca rô \"khăn trải bàn\" được chỉnh lại kích thước và màu sắc hoạ tiết để trông hiện đại và bắt mắt hơn.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 2\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/6f89d70e63a7c05bbf81daddf80dbd0b.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:990px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" title=\"Cập nhật ngay xu hướng sọc ca rô vào tủ quần áo hình ảnh 2\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Nếu nghĩ tới sọc ca rô là bạn nghĩ ngay đến áo sơ mi, thì với xu hướng thời trang mùa đầu năm nay, các tín đồ sẽ phải phải nhìn lại. Từ chân váy bèo nhún, áo kiểu, áo khoác và cả phụ kiện đều được phủ lên hoạ tiết sọc caro nhiều màu sắc thú vị, cá tính và độc đáo. &nbsp;Từ Balenciaga, Sonia Rykiel đến Victoria Beckham đều lăng xê hoạ tiết ca rô trên sàn diễn. Các nhà thiết kế đều lấy cảm hứng từ sọc ca rô cổ điển từ thập niên 1970, xào nấu lại để khiến chúng trông hiện đại và bắt mắt hơn, dễ mặc và phù hợp với phong cách chung của từng bộ sưu tập. &nbsp;Sọc Gingham vẫn được các nhà thiết kế chọn lựa trong mùa này.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 3\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/9bd230c3c7b8783c3317004ce2ccbcad.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:841px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:659.7px\" title=\"Cập nhật ngay xu hướng sọc ca rô vào tủ quần áo hình ảnh 3\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Kiểu sọc này khá trông quen mắt. Chính xác là hoạ tiết sọc vuông khá nổi tiếng của những năm 1960 được cách điệu lại cho phù hợp với xu hướng mới.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 4\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/1070d21357fd6a671af19995a6cfdbd8.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:990px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Các kiểu sọc được biến tấu với nhiều màu kết hợp để cho ra các tổng thể hoạ tiết thanh lịch và hiện đại</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">.\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 5\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/1351cc10362c437407a05207f5a99a81.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:878px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:659.67px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Sọc Gingham vừa dễ mặc, vừa dễ phối đồ để đi dạo phố.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 6\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/33007a5ab150fa8ff103a75de3a0b97d.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:993px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:659.661px\" title=\"Cập nhật ngay xu hướng sọc ca rô vào tủ quần áo hình ảnh 6\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Thêm vào màu xanh dương nổi bật, hoạ tiết sọc ca rô đều đặn như thế này vẫn trông rất hiện đại.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 7\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/55808a096956d18b4dd5b4ee1e175f64.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:990px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Sọc ca rô còn được đưa lên áo khoác và các trang phục khác, thêm sắc màu cho xu hướng này trong mùa xuân hè 2018.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 8\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/642712232.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:660px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" title=\"Cập nhật ngay xu hướng sọc ca rô vào tủ quần áo hình ảnh 8\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Chiếc áo sơ mi lấy cảm hứng từ hoạ tiết của các chàng trai từ 50 năm trước, kết hợp với chân váy sọc ca rô. Các cô gái có thể diện nguyên cây ca rô bằng cách phối hợp hai sắc độ và mật độ ca rô khác nhau như thế này.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 9\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/642712234.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:660px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Áo dệt kim ca rô chéo và chân váy hoạ tiết ca rô đặc trưng của nhà Burberry vẫn luôn chiếm lĩnh xu thế.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 10\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/642712241.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:660px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" title=\"Cập nhật ngay xu hướng sọc ca rô vào tủ quần áo hình ảnh 10\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Màu vàng và đen kết hợp với nhau trong hoạ tiết ca rô độc đáo.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 11\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/a93ae6cfb873e130503e012c8c8445c3.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:990px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Với chất vải tweed, nhà Channel lại có thể dệt thành hoạ tiết ca rô nổi khối 3D khiến giới mộ điệu mê mẩn.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background:transparent; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<div class=\"photoset-item\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-size-adjust: 100%;\"><img alt=\"Cap nhat ngay xu huong soc ca ro vao tu quan ao hinh anh 12\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/ycgmvjvj/2018_03_20/e811ec2a6d164b2286c57bf6ed6eccc5.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:1011px; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:659.958px\" /></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Bèo nhún kết hợp với hoạ tiết ca rô là công thức được nhiều nhà thiết kế lựa chọn. Phong cách này cũng làm say mê giới mộ điệu trên toàn thế giới và cả Việt Nam.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>', '2018-06-16 11:05:05', '2018-06-16 11:50:41'),
(13, 'Màu kem tươi chiếm ngôi đầu bảng xu hướng thời trang xuân hè 2018', 'mau-kem-tuoi-chiem-ngoi-dau-bang-xu-huong-thoi-trang-xuan-he-2018', 'FcUqO-112.jpg', 'Xuân-Hè luôn là dịp cho các thương hiệu thời trang lớn tung ra tất cả độc chiêu của mình qua hàng loạt bộ sưu tập như...', '<p><strong>Xuân-Hè luôn là dịp cho các thương hiệu thời trang lớn tung ra tất cả độc chiêu của mình qua hàng loạt bộ sưu tập như Gucci, Chanel, Balenciaga, Dolce &amp; Gabbana,… Để đón đầu xu hướng, các tín đồ thời trang trong và ngoài nước luôn cập nhật liên tục các xu hướng về màu sắc, họa tiết cũng như những phụ kiện đi kèm để tạo nên những outfit dạo phố, tiệc tùng vô cùng nổi bật.</strong></p>\r\n\r\n<p>Hòa vào không khí sôi động của làng thời trang xuân hè này, mời các bạn điểm qua một vài nét nổi bật của xu hướng thời trang xuân-hè năm 2018 này.</p>\r\n\r\n<p><strong>1. ICE CREAM COLOURS</strong></p>\r\n\r\n<p>Ultra Violet lên ngôi nhưng sắc tím ấy luôn là một bài toán khó. May mắn thay, các nhà thiết kế đã chiều lòng các tín đồ thời trang khi đồng loạt lăng xê bảng màu \"kem tươi” ngon mắt, xoa dịu đi thời tiết nóng bức mùa hè này với những màu sắc nổi bật như: Rosy Pink, Lemon Cream, Vanilla,…</p>\r\n\r\n<p><img alt=\"\" src=\"http://premi3r.vn/images_upload/111.png\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" /></p>\r\n\r\n<p><em>Mở màn với ba tông màu kem dịu mắt: Lemon cream, Vannila, Rosy Pink đến từ thương hiệu Ulla Johnson.</em>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://premi3r.vn/images_upload/2.png\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" /></p>\r\n\r\n<p><em>Victoria Beckham thổi vào những thiết kế sự nhẹ dịu, ngọt ngào và bắt mắt qua những thiết kế đa dạng về phong cách.</em></p>\r\n\r\n<p><img alt=\"\" src=\"http://premi3r.vn/images_upload/elle-vietnam-Ermanno-Scervino-S18.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" />&nbsp;</p>\r\n\r\n<p><em>BST Xuân – Hè 2018 của thương hiệuErmanno Scervino đã mang đến những mẫu thiết kế menswear đặc trưng thanh lịch với những tông màu dịu mắt.</em></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">\r\n<p>Các nhà thiết kế đã sử dụng những tông màu nguyên gốc, đặc biệt là sự khéo léo trong từng đường kim mũi chỉ, từng cách chọn chất liệu để phối hợp với nhau như da bóng, nỉ mịn, lụa bóng mờ,… để tạo nên những \"cây kem” ngon mắt, làm hài lòng những tín đồ thời trang. Thanh lịch - thời thượng - khác biệt luôn là những xu hướng được các nhà thiết kế khai thác từ những gam màu này.</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/112.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" /></div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">\r\n<p>Trên sàn diễn thời trang, mỗi bộ thiết kế đẹp rạng ngời là thế, nhưng nếu áp dụng công thức \"cây kem mùa hè” vào những bộ outfit xuống phố thì sao? Câu trả lời là 100% các cô gái khác sẽ phải ghen tỵ khi bạn biến hóa cùng công thức này.</p>\r\n\r\n<p>&nbsp;</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/113.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" /></div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">Dù bạn diện nhiều lớp hay chỉ đơn thuần là áo thun với váy hoặc quần jeans, thì bảng màu pastel luôn luôn tôn lên được nét nhẹ nhàng, thanh toát trong từng kiểu dáng outfit mà bạn chọn. Sẵn sàng xuống phố với công thức \"ice cream girl” đi, vì đảm bảo sắc hè 2018 này chắc chắn dành riêng bạn đấy!</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>2. POLKA DOT (HỌA TIẾT CHẤM BI)</strong></p>\r\n\r\n<p>Xuân - Hè 2018 đã trở lại với họa tiết chấm bi cổ điển của những cô nàng Pop-art. Khắp các sàn runway, Pop-art lại trở lại với nhiều kiểu dáng mới lạ như tay phồng, váy xẻ sâu, nhấn mạnh từng chi tiết độc đáo mà hoạt tiết chấm bi mang lại.</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/114.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" />\r\n<p>Họa tiết chấm bi khi được đưa lên sàn runway quốc tế trở nên độc đáo và hấp dẫn hơn bao giờ hết, còn với trang phục thường ngày thì sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/115.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" /></div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">Câu trả lời nằm ở chính những kiểu dáng váy ôm gọn nhẹ, một chút bèo nhún điểm xuyến trên phần cổ áo hay phần đuôi váy cùng họa tiết chấm bi trải dài. Trông các cô gái nhẹ nhàng và đáng yêu hết mực.\r\n<p><img alt=\"\" src=\"http://premi3r.vn/images_upload/116.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" /></p>\r\n\r\n<p>Với sự \"lên ngôi” của phong cách thời trang những năm thập niên 80, họa tiết chấm bị tuy đơn giản nhưng lại làm các cô gái phải mê mẩn bởi những chấm bi to hay nhỏ nhuyễn đều có thể tôn lên dáng vẻ và tạo cho chúng ta cảm giác nhẹ nhàng, hết sức phù hợp cho những ngày hè nắng rực thế này.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n<strong>3. FLORAL PRINTS (HỌA TIẾT HOA)</strong></div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">Họa tiết hoa từ lâu luôn gây cảm tình với phái đẹp bởi sự điệu đà, nữ tính. Trong Xuân/Hè 2018, những bản in hoa được các nhà thiết kế biến hóa trên nền vải chiffon (loại vải mỏng và nhẹ) thoáng khí , màu sắc trang nhã kèm theo những nét phá cách độc đáo của các nhà thiết kế. Đáng chú ý nhất, trên các show diễn runway lớn như Self Portrait, Tadashi Shoji, và Prabal Gurung, những mẫu váy họa tiết hoa in nổi bật tuy có phần dày đặc nhưng vẫn tạo cảm giác mềm mại, được cộng hưởng bởi những đường cắt xẻ sâu của váy. Tất cả như một bức tranh hòa quyện giữa sự lãng mạn, thanh lịch trên nền thiết kế hiện đại.&nbsp;\r\n<p>&nbsp;</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/118.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" />\r\n<p>Không chỉ tại Mỹ, trend Floral Prints còn được lăng xê tối đa tại Hàn Quốc. Điển hình nhất là Black Pink đã sử dụng khéo léo họa tiết hoa trên các trang phục trong MV hoặc show trình diễn của mình.</p>\r\n\r\n<p>&nbsp;</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/jennie-outfit-2.png\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" />\r\n<p>Để tăng thêm phần cá tính, một chiếc dây nịt bản lớn sẽ là phụ kiện đi kèm tối ưu cùng chiếc váy hoa mềm mại. Các cô gái cá tính không còn ngần ngại thử sức với trend Floral Prints này nữa rồi nhé!</p>\r\n\r\n<p>&nbsp;</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/8.png\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" />\r\n<p>Chính xác hơn, những chiếc váy hoa đích thị là người bạn thân nhất với thời tiết mùa hè nóng nực, khi diện chúng cùng với giày cao gót. Tuy nhiên nếu thay bằng một đôi sneaker thì cũng không tồi nhỉ? Với các cô gái năng động, giày sneaker mang lại cảm giác thoải mái dễ chịu hơn.</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/10.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" /></div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">&nbsp;</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">&nbsp;</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\"><strong>4.&nbsp;</strong><strong>STRIPE PATTERN (HỌA TIẾT SỌC)</strong></div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\">Không còn xa lạ gì với những họa tiết sọc ngang hay dọc vì năm nào họa tiết này luôn được xếp vào danh sách \"timeless patterns” – những họa tiết không bao giờ lỗi thời. Và khi sọc dọc candy stripe nổi lên như \"diều gặp gió” trong năm 2018 này, các nhà thiết kế đã nhanh chóng chỉnh sửa và nâng cấp sản phẩm của mình lên một tầm cao mới.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: Roboto, sans-serif; font-size: 14px;\"><img alt=\"\" src=\"http://premi3r.vn/images_upload/11.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" />\r\n<p>Sự xuất hiện của candy stripe được thay đổi theo tông màu hồng đậm đến hồng đào hoặc hồng phớt nhạt. Sự nhẹ nhàng của bảng màu này càng làm nổi bật các họa tiết sọc ngang một cách tinh tế. Đây chính là một trong những xu hướng Xuân/Hè 2018 gây ra một hiện tượng thời trang toàn cầu.</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/12.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" />\r\n<p>Thời trang vận đông không theo sự sắp xếp nào cả. Chính vì điều đó, các nhà thiết kế đã thổi phồng lên, tạo ra sự hài hòa từ màu sắc từ trung tính đến cầu vồng, từ các kiểu dáng thời trang khác nhau đến cách phối tinh tế cùng phụ kiện cần thiết chỉ bằng vài đường họa tiết stripe đơn giản.</p>\r\n<img alt=\"\" src=\"http://premi3r.vn/images_upload/elle-vietnam-Ulla-Johnson-S18.jpg\" style=\"border:0px; box-sizing:border-box; display:block; margin:0px; padding:0px 0px 10px; vertical-align:middle; width:730px\" />\r\n<p>Không chỉ nổi bật trên sàn diễn, khi diện họa tiết stripe này xuống phố, những chiếc váy mỏng nhẹ hay những mẫu áo thun sọc đơn giản vẫn giúp bạn trông nhẹ nhàng, thoải mái, và nổi bật giữa đám đông.</p>\r\n\r\n<p>Xu hướng thời trang luôn đổi mới không ngừng. Để đón đầu xu hướng, trở thành một tín đồ thời trang chính hiệu, bạn hãy cập nhật ngay những màu sắc, họa tiết này cho các outfit xuân-hè tiếp theo để tủ đồ nhà mình thêm xinh, thêm tươi các bạn nhé!</p>\r\n</div>', '2018-06-16 22:41:54', '2018-06-16 23:23:26'),
(14, 'Điểm lại 5 xu hướng thời trang Xuân – Hè nổi bật', 'diem-lai-5-xu-huong-thoi-trang-xuan-he-noi-bat', 'heYWZ-elle-viet-nam-xu-huong-thoi-trang-1-edit.jpg', 'Chất liệu trong suốt, đường cắt phóng khoáng, cách phối màu đầy thách thức là những yếu tố làm nên một mùa thời trang Xuân - Hè sôi động và tràn đầy sức sống...', '<div class=\"content-detail__short-desc\" style=\"box-sizing: border-box; font-size: 1.125em; color: rgb(0, 0, 0); line-height: 25px; font-style: italic; padding-top: 45px; padding-bottom: 0px; font-family: &quot;Helvetica Neue&quot;, Arial, sans-serif;\">\r\n<p><span style=\"font-size:18px\">Chất liệu trong suốt, đường cắt phóng khoáng, cách phối màu đầy thách thức là những yếu tố làm nên một mùa thời trang Xuân – Hè sôi động và tràn đầy sức sống.</span></p>\r\n</div>\r\n\r\n<div class=\"content-detail__content\" style=\"box-sizing: border-box; font-family: &quot;Helvetica Neue&quot;, Arial, sans-serif; font-size: 16px;\">\r\n<p>Cùng March Fashion điểm quạ xu hướng thời trang Xuân- Hè 2018.</p>\r\n\r\n<h3><strong>THẤP THOÁNG DÁNG NGÀ</strong></h3>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">Bứt phá khỏi hình ảnh nữ tính quyến rũ, trang phục trong suốt trở nên cá tính nhưng vẫn không kém phần thanh lịch trong các thiết kế của Dior, Hugo Boss và Fendi. Chiffon, voan mỏng manh được phù phép linh hoạt với nhiều món đồ thời trang như&nbsp;<a href=\"http://www.elle.vn/xu-huong-phong-cach/5-kieu-mix-match-doc-dao-cung-chan-vay-but-chi\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\">chân váy</a>, áo khoác, mũ và áo lót sành điệu.</p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-1.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 1\" class=\"aligncenter wp-image-252847\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-1.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 1\" /></a></p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-2.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 2\" class=\"aligncenter wp-image-252848\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-2.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 2\" /></a></p>\r\n\r\n<h3 style=\"text-align:justify\">&nbsp;</h3>\r\n\r\n<h3 style=\"text-align:justify\"><strong>NGHIÊNG NGHIÊNG BỜ VAI</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Đường nét tinh tế của bờ vai nữ tính được tôn vinh bằng đường cắt xéo mạnh mẽ nơi cổ áo. Sự ngỗ nghịnh của chi tiết lệch vai trong BST của Calvin Klein, Marc Jacobs hay Alexander Wang tạo thêm phần duyên dáng và quyến rũ, cho dù đó là chiếc váy dạ hội quý phái hay đầm mini gợi cảm.</p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-3.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 3\" class=\"aligncenter wp-image-252849\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-3.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 3\" /></a></p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-4.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 4\" class=\"aligncenter wp-image-252850\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-4.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 4\" /></a></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>BỮA TIỆC Ô KẺ</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Nếu mùa Thu – Đông 2017 chứng kiến sự trở lại của&nbsp;<a href=\"http://www.elle.vn/bo-hinh-cua-elle/bo-anh-thoi-trang-o-ke-mau-xuan\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\">họa tiết ca rô&nbsp;</a>trên bộ vest xám thì Xuân-Hè 2018 mang lại cho ca rô màu sắc tươi mới và tinh nghịch. Emporio Armani, Balenciaga hay Calvin Klein đều biến tấu họa tiết này trên nhiều phom dáng và kích cỡ.</p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-5.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 5\" class=\"aligncenter wp-image-252851\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-5.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 5\" /></a></p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-6.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 6\" class=\"aligncenter wp-image-252852\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-6.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 6\" /></a></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>CUA-RƠ SÀNH ĐIÊU</strong></p>\r\n\r\n<p style=\"text-align:justify\">Khó có thể tin được chính ngôi sao truyền hình thực tế Kim Kardashian là người “báo động” về trào lưu quần shorts cua-rơ. Dolce &amp; Gabbana, Nina Ricci hay Off-White mang đến những biến tấu riêng biệt cho kiểu quần này. Không kể đến tính thể thao của loại quần shorts vốn dành cho những tay đua xe đạp, các chất liệu đa dạng như kaki, thun và denim càng thú vị hơn khi kết hợp cùng áo crop top hay áo bomber.</p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-7.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 7\" class=\"aligncenter wp-image-252853\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-7.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 7\" /></a></p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-8.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 8\" class=\"aligncenter wp-image-252854\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-8.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 8\" /></a></p>\r\n\r\n<h3 style=\"text-align:justify\">&nbsp;</h3>\r\n\r\n<h3 style=\"text-align:justify\"><strong>TRENCH COAT HỒI SINH</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Khởi đầu từ Maison Margiela,&nbsp;<a href=\"http://www.elle.vn/the-gioi-thoi-trang/ao-trench-coat-tre-mai-khong-gia\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out; outline: 0px; outline-offset: -2px;\">áo khoác trench coat</a>&nbsp;với những đường cắt sắc bén và cách kiến tạo bố cục độc đáo trở thành tâm điểm mới. Không giới hạn trong chất liệu kaki quen thuộc, Valentino, Céline hay Max Mara lựa chọn phá cách với voan và kaki mềm, tạo ra hình ảnh mới mềm mại hơn cho kiểu áo này. Đường cắt táo bạo, điểm nhấn ở chi tiết cut-out hay tua rua khiến trench coat trở nên trẻ trung và khác biệt.</p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-9.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 9\" class=\"aligncenter wp-image-252855\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-9.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 9\" /></a></p>\r\n\r\n<p><a class=\"auto_magnific\" href=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-10.jpg\" rel=\"article_gallery\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(125, 158, 198); text-decoration-line: none; transition: color 0.3s ease-out;\"><img alt=\"xu hướng thời trang 10\" class=\"aligncenter wp-image-252856\" src=\"http://www.elle.vn/wp-content/uploads/2018/03/14/elle-viet-nam-xu-huong-thoi-trang-10.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin:5px auto; max-width:100%; vertical-align:middle; width:730px\" title=\"xu hướng thời trang 10\" /></a></p>', '2018-06-16 22:48:58', '2018-06-16 23:21:22');
